INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1447118, 'Bepridil', 'Mivacurium', 'Moderate', 'Some calcium channel blockers may enhance the neuromuscular blocking properties of nondepolarizing muscle relaxants. The mechanism is not known, but may involve blockage of calcium channels on skeletal muscle. Data are available for verapamil, diltiazem, nicardipine, atracurium, vecuronium, and pancuronium.', 'DDInter', 1767317476, 'Patients should be closely monitored for prolonged neuromuscular blockade. Reduced doses of muscle relaxants may be necessary. Profound neuromuscular blockade may be reversed by neostigmine or edrophonium.', 'Synergism', 'Patients should be closely monitored for prolonged neuromuscular blockade.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1447119, 'Carteolol', 'Mivacurium', 'Moderate', 'Neuromuscular blocking agents and beta-blockers may have additive cardiovascular depressant effects. Limited data suggest that beta-blockers may modestly potentiate the pharmacologic effects of neuromuscular blocking agents. The exact mechanism of interaction is unknown but may involve effects in the postsynaptic membrane, as beta-blockers alone have been reported to exacerbate or unmask myasthenia gravis.', 'DDInter', 1767317476, 'Clinicians should recognize the potential for altered effects of neuromuscular blocking agents in the presence of beta-blockers. Respiratory and cardiovascular status should be closely monitored.', 'Synergism', 'Clinicians should recognize the potential for altered effects of neuromuscular blocking agents in the presence of beta-blockers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1447120, 'Demeclocycline', 'Mivacurium', 'Moderate', 'Some drugs may enhance the effects of neuromuscular blocking agents.', 'DDInter', 1767317476, 'Observation for profound and prolonged respiratory depression and muscle weakness is recommended if these drugs must be used together.', 'Synergism', 'Observation for profound and prolonged respiratory depression and muscle weakness is recommended if these drugs must be used together.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1447121, 'Desflurane', 'Mivacurium', 'Moderate', 'Inhalation anesthetics may potentiate the effects of nondepolarizing muscle relaxants. Long-acting muscle relaxants such as pancuronium and d-tubocurarine are more affected than other agents.', 'DDInter', 1767317476, 'The muscle relaxant dosage may need to be reduced when inhalation anesthetics are used, especially during longer procedures. In general, inhalation anesthetics should be administered only by health care providers specially trained in the use of these agents and in the care of anesthetized patients.', 'Synergism', 'The muscle relaxant dosage may need to be reduced when inhalation anesthetics are used, especially during longer procedures.', NULL, NULL, 'Moderate', 'pharmacodynamic');
