INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1447251, 'Fosphenytoin', 'Modafinil', 'Moderate', 'Coadministration with modafinil (the racemate) or armodafinil (the R-enantiomer) may increase the plasma concentrations of drugs that are primarily metabolized by the CYP450 2C19 isoenzyme. The mechanism is decreased clearance due to inhibition of CYP450 2C19 activity by modafinil and armodafinil.', 'DDInter', 1767317476, 'Caution is advised if modafinil or armodafinil must be used concurrently with medications that undergo metabolism by CYP450 2C19, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever modafinil or armodafinil is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if modafinil or armodafinil must be used concurrently with medications that undergo metabolism by CYP450 2C19, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1447252, 'Fostamatinib', 'Modafinil', 'Moderate', 'Coadministration of fostamatinib with inducers of CYP450 3A4 may decrease exposure to the active metabolite known as R406, the predominant moiety in the systemic circulation following fostamatinib administration. Fostamatinib is metabolized in the gut by alkaline phosphatase to R406, which then undergoes oxidation via CYP450 3A4 and glucuronidation via UGT1A9.', 'DDInter', 1767317476, 'The potential for diminished pharmacologic effects of fostamatinib should be considered during coadministration with CYP450 3A4 inducers. Alternative treatments may be required if an interaction is suspected.', 'Metabolism', 'The potential for diminished pharmacologic effects of fostamatinib should be considered during coadministration with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1447253, 'Fostemsavir', 'Modafinil', 'Minor', 'Coadministration of fostemsavir with moderate or weak CYP450 3A4 inducers may decrease the plasma concentrations of temsavir, the active moiety of fostemsavir. According to the prescribing information, temsavir is a substrate of CYP450 3A4, esterases, P-glycoprotein (P-gp), and breast cancer resistance protein (BCRP).', 'DDInter', 1767317476, '-', 'Metabolism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
