INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1447256, 'Glasdegib', 'Modafinil', 'Major', 'Coadministration with moderate inducers of CYP450 3A4 may decrease the plasma concentrations of glasdegib, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317476, 'Concomitant use of glasdegib with moderate CYP450 3A4 inducers should generally be avoided. If concomitant use cannot be avoided, the glasdegib dose should be increased as tolerated from 100 mg once a day to 200 mg once a day or from 50 mg once a day to 100 mg once a day. Once the moderate CYP450 3A4 inducer has been discontinued for 7 days, the glasdegib dose should return to the dose taken prior to initiating the moderate CYP450 3A4 inducer.', 'Metabolism', 'Concomitant use of glasdegib with moderate CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
