INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1447293, 'Modafinil', 'Lurbinectedin', 'Major', 'Coadministration with a strong or moderate CYP450 3A4 inducer is predicted to decrease lurbinectedin systemic exposure, which may reduce its efficacy.', 'DDInter', 1767317476, 'Concomitant use of lurbinectedin and strong or moderate CYP450 3A4 inducers should generally be avoided.', 'Metabolism', 'Concomitant use of lurbinectedin and strong or moderate CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
