INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1447294, 'Modafinil', 'Macimorelin', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of macimorelin, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317476, 'The prescribing information for macimorelin recommends discontinuing potent CYP450 3A4 inducers (e.g., carbamazepine, enzalutamide, lumacaftor, mitotane, phenobarbital, phenytoin, rifamycins, St. John''s wort) as well as some moderate ones (e.g., bosentan, efavirenz, etravirine, modafinil) prior to macimorelin administration. A sufficient washout period following discontinuation of the inducers is also advised before using macimorelin. No recommendations are available for other, less potent CYP450 3A4 inducers; however, it may be appropriate to follow the same precaution.', 'Metabolism', 'The prescribing information for macimorelin recommends discontinuing potent CYP450 3A4 inducers (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1447295, 'Modafinil', 'Macitentan', 'Minor', 'Coadministration with modafinil (the racemate) or armodafinil (the R-enantiomer) may decrease the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. Modafinil and armodafinil are modest inducers of CYP450 3A4, and pharmacokinetic studies suggest that their effects may be primarily intestinal rather than hepatic. Thus, clinically significant interactions would most likely be expected with drugs that have low oral bioavailability due to significant intestinal CYP450 3A4-mediated first-pass metabolism (e.g., buspirone, cyclosporine, lovastatin, midazolam, saquinavir, simvastatin, sirolimus, tacrolimus, triazolam, calcium channel blockers). However, the potential for interaction should be considered with any drug metabolized by CYP450 3A4, especially given the high degree of interpatient variability with respect to CYP450-mediated metabolism. Pharmacologic response to these drugs may be altered and should be monitored more closely whenever modafinil or armodafinil is added to or withdrawn from therapy. Dosage adjustments may be required if an interaction is suspected.', 'DDInter', 1767317476, '-', 'Metabolism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1447296, 'Modafinil', 'Maraviroc', 'Moderate', 'Coadministration with inducers of the CYP450 3A4 isoenzyme and/or P-glycoprotein efflux transporter may decrease the plasma concentrations of maraviroc, which is a substrate of both.', 'DDInter', 1767317476, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, caution is advised if maraviroc is used with CYP450 3A4 and/or P-glycoprotein inducers. A dosage adjustment for maraviroc may be necessary if an interaction is suspected.', 'Metabolism', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, caution is advised if maraviroc is used with CYP450 3A4 and/or P-glycoprotein inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1447297, 'Modafinil', 'Medroxyprogesterone acetate', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations and pharmacologic effects of medroxyprogesterone, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317476, 'Pharmacologic response to medroxyprogesterone should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the dosage adjusted as necessary. When administered as the depot formulation for contraception, no dosage adjustment for medroxyprogesterone is currently recommended during coadministration with CYP450 3A4 inducers.', 'Metabolism', 'Pharmacologic response to medroxyprogesterone should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the dosage adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
