INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1447328, 'Osilodrostat', 'Modafinil', 'Moderate', 'Coadministration with inducers of CYP450 3A4 and/or 2B6 may decrease the plasma concentrations of osilodrostat, which is partially metabolized by these isoenzymes. According to the product labeling, multiple CYP450 isoenzymes (CYP450 3A4, 2B6, and 2D6) and UDP-glucuronosyltransferases contribute to osilodrostat metabolism, and no single pathway contributes greater than 25% to the total clearance. Some of the known CYP450 3A4 and 2B6 inducers also induce UDP-glucuronosyltransferases.', 'DDInter', 1767317476, 'The potential for diminished pharmacologic effects of osilodrostat should be considered during concomitant use of a CYP450 3A4 and/or 2B6 inducer. An increase in the dosage of osilodrostat may be required. Dosage adjustments should be based on clinical response and tolerance. Patients should have regular monitoring of 24-hour urine free cortisol and serum or plasma cortisol during treatment, as well as regular evaluations of their signs and symptoms. A reduction in dosage of osilodrostat may be required if the CYP450 3A4 and/or 2B6 inducer is discontinued during treatment with osilodrostat.', 'Metabolism', 'The potential for diminished pharmacologic effects of osilodrostat should be considered during concomitant use of a CYP450 3A4 and/or 2B6 inducer.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1447329, 'Osimertinib', 'Modafinil', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of osimertinib, which has been shown in vitro to be primarily metabolized by the isoenzyme. Reduced therapeutic efficacy of osimertinib may occur. However, no pharmacokinetic data are available from drug interaction studies.', 'DDInter', 1767317476, 'The potential for diminished pharmacologic effects of osimertinib should be considered during coadministration with CYP450 3A4 inducers. Alternative treatments may be required if an interaction is suspected.', 'Metabolism', 'The potential for diminished pharmacologic effects of osimertinib should be considered during coadministration with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
