INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1447370, 'Rilpivirine', 'Modafinil', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of rilpivirine, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317476, 'To minimize the risk of reduced viral susceptibility and resistance development associated with subtherapeutic levels of rilpivirine, caution and clinical monitoring may be advisable if CYP450 3A4 inducers are coadministered with rilpivirine.', 'Metabolism', 'To minimize the risk of reduced viral susceptibility and resistance development associated with subtherapeutic levels of rilpivirine, caution and clinical monitoring may be advisable if CYP450 3A4 inducers are coadministered with rilpivirine.', NULL, NULL, 'Moderate', 'pharmacodynamic');
