INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1447419, 'Triazolam', 'Modafinil', 'Moderate', 'Coadministration with modafinil (the racemate) or armodafinil (the R-enantiomer) may decrease the plasma concentrations and efficacy of triazolam. The mechanism involves induction of intestinal CYP450 3A4-mediated first-pass metabolism by modafinil and armodafinil, with possible secondary induction of hepatic 3A4 metabolism.', 'DDInter', 1767317476, 'Pharmacologic response to triazolam should be monitored more closely whenever modafinil or armodafinil is added to or withdrawn from therapy, and the triazolam dosage adjusted as necessary.', 'Metabolism', 'Pharmacologic response to triazolam should be monitored more closely whenever modafinil or armodafinil is added to or withdrawn from therapy, and the triazolam dosage adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1447420, 'Trimipramine', 'Modafinil', 'Moderate', 'Coadministration with modafinil (the racemate) or armodafinil (the R-enantiomer) may increase the plasma concentrations of tricyclic antidepressants (TCAs) in patients who are deficient in CYP450 2D6 (approximately 7% to 10% of the Caucasian population; similar or lower in other populations). Modafinil and armodafinil are modest inhibitors of CYP450 2C19, an isoenzyme that provides an ancillary pathway for the metabolism of certain TCAs normally metabolized by CYP450 2D6 such as clomipramine or desipramine.', 'DDInter', 1767317476, 'Caution is advised if tricyclic antidepressants are coadministered with modafinil or armodafinil in CYP450 2D6-deficient patients. Pharmacologic effects and serum TCA levels should be monitored more closely whenever modafinil or armodafinil is added to or withdrawn from therapy, and the TCA dosage adjusted accordingly. Patients should be advised to notify their physician if they experience possible signs of TCA toxicity such as excessive drowsiness, restlessness, agitation, ataxia, blurred vision, tachycardia, arrhythmia, and seizures.', 'Metabolism', 'Caution is advised if tricyclic antidepressants are coadministered with modafinil or armodafinil in CYP450 2D6-deficient patients.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1447421, 'Troglitazone', 'Modafinil', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of modafinil (the racemate) and armodafinil (the R-enantiomer), which are both partially metabolized by the isoenzyme.', 'DDInter', 1767317476, 'Pharmacologic response to modafinil or armodafinil should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy. Dose adjustments may be required if an interaction is suspected.', 'Metabolism', 'Pharmacologic response to modafinil or armodafinil should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1447422, 'Troleandomycin', 'Modafinil', 'Minor', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of modafinil (the racemate) and armodafinil (the R-enantiomer), which are both partially metabolized by the isoenzyme. Conversely, the plasma levels of some of these inhibitors may decrease, since many of them are also substrates of CYP450 3A4, and modafinil and armodafinil have been found to be modest inducers of CYP450 3A4. The clinical significance of this potential interaction is unknown. Clinical monitoring for altered effects of modafinil and armodafinil as well as the CYP450 3A4 inhibitor may be appropriate following addition or withdrawal of one or the other drug.', 'DDInter', 1767317476, '-', 'Metabolism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1447423, 'Tucatinib', 'Modafinil', 'Moderate', 'Coadministration with CYP450 3A4 inducers may decrease the plasma concentrations of tucatinib, which is primarily metabolized by CYP450 2C8 and to a lesser extent by CYP450 3A4.', 'DDInter', 1767317476, 'The potential for diminished pharmacologic effects of tucatinib should be considered during coadministration with CYP450 3A4 inducers. Alternative treatments may be required if an interaction is suspected.', 'Metabolism', 'The potential for diminished pharmacologic effects of tucatinib should be considered during coadministration with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1447424, 'Ubrogepant', 'Modafinil', 'Moderate', 'Coadministration with moderate or weak inducers of CYP450 3A4 may decrease the plasma concentrations of ubrogepant, which is primarily metabolized by the isoenzyme. Dedicated drug interaction studies have not been conducted to assess concomitant use of ubrogepant with moderate or weak CYP450 3A4 inducers.', 'DDInter', 1767317476, 'The manufacturer recommends an initial ubrogepant dose of 100 mg when coadministered with moderate or weak CYP450 3A4 inducers. If needed, a second 100 mg dose of ubrogepant may be administered at least 2 hours after the initial dose.', 'Metabolism', 'The manufacturer recommends an initial ubrogepant dose of 100 mg when coadministered with moderate or weak CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1447425, 'Ulipristal', 'Modafinil', 'Moderate', 'The concomitant use of CYP450 3A4 inducers may decrease the plasma concentrations and efficacy of ulipristal, which is primarily metabolized by the isoenzyme. Induction of CYP450 3A4 may last for up to 4 weeks after discontinuation of the enzyme inducer.', 'DDInter', 1767317476, 'Concomitant use of ulipristal with CYP450 3A4 inducers should be avoided if possible and nonhormonal emergency contraception should be considered.', 'Metabolism', 'Concomitant use of ulipristal with CYP450 3A4 inducers should be avoided if possible and nonhormonal emergency contraception should be considered.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1447426, 'Upadacitinib', 'Modafinil', 'Moderate', 'Coadministration with moderate inducers of CYP450 3A4 may decrease the plasma concentrations of upadacitinib, which is primarily metabolized by the isoenzyme. The interaction may result in loss of or reduced clinical response.', 'DDInter', 1767317476, 'Caution is recommended if upadacitinib is used with moderate CYP450 3A4 inducers. Pharmacologic effects of upadacitinib should be monitored more closely whenever a moderate CYP450 3A4 inducer is added to or withdrawn from therapy.', 'Metabolism', 'Caution is recommended if upadacitinib is used with moderate CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1447427, 'Valdecoxib', 'Modafinil', 'Minor', 'Coadministration with modafinil (the racemate) or armodafinil (the R-enantiomer) may decrease the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. Modafinil and armodafinil are modest inducers of CYP450 3A4, and pharmacokinetic studies suggest that their effects may be primarily intestinal rather than hepatic. Thus, clinically significant interactions would most likely be expected with drugs that have low oral bioavailability due to significant intestinal CYP450 3A4-mediated first-pass metabolism (e.g., buspirone, cyclosporine, lovastatin, midazolam, saquinavir, simvastatin, sirolimus, tacrolimus, triazolam, calcium channel blockers). However, the potential for interaction should be considered with any drug metabolized by CYP450 3A4, especially given the high degree of interpatient variability with respect to CYP450-mediated metabolism. Pharmacologic response to these drugs may be altered and should be monitored more closely whenever modafinil or armodafinil is added to or withdrawn from therapy. Dosage adjustments may be required if an interaction is suspected.', 'DDInter', 1767317476, '-', 'Metabolism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1447428, 'Vemurafenib', 'Modafinil', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of vemurafenib, which has been shown in vitro to be a substrate of the isoenzyme.', 'DDInter', 1767317476, 'The potential for diminished pharmacologic effects of vemurafenib should be considered during coadministration with CYP450 3A4 inducers. Alternative treatments or a dose adjustment of vemurafenib may be required if an interaction is suspected.', 'Metabolism', 'The potential for diminished pharmacologic effects of vemurafenib should be considered during coadministration with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
