INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1447501, 'Interferon alfa-2a, Recombinant', 'Moexipril', 'Moderate', 'The concomitant use of recombinant interferon-alpha-2a and some angiotensin converting enzyme (ACE) inhibitors has been associated with a higher incidence of granulocytopenia than either drug used alone in patients with cryoglobulinemia. The mechanism is unknown but may be related to an autoimmune response. It is also unknown whether patients with other conditions may be affected. Data exist for enalapril and captopril.', 'DDInter', 1767317476, 'Until more information is available, close hematologic monitoring is recommended.', 'Synergism', 'Until more information is available, close hematologic monitoring is recommended.', NULL, NULL, 'Moderate', 'pharmacodynamic');
