INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1447544, 'Nitroglycerin', 'Moexipril', 'Moderate', 'Angiotensin converting enzyme (ACE) inhibitors may enhance the vasodilatory and hypotensive effects of nitroglycerin. Data have also shown that captopril can prevent nitrate tolerance. ACE inhibitors can decrease systemic vascular resistance and cardiac work, further enhancing the effectiveness of nitroglycerin.', 'DDInter', 1767317476, 'In general, this combination is used to clinical advantage; however, some manufacturers recommend that nitrates and other vasodilators should be discontinued before starting ACE inhibitors or resumed at a reduced dose. Blood pressure monitoring is advisable.', 'Synergism', 'In general, this combination is used to clinical advantage; however, some manufacturers recommend that nitrates and other vasodilators should be discontinued before starting ACE inhibitors or resumed at a reduced dose.', NULL, NULL, 'Moderate', 'pharmacodynamic');
