INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1447592, 'Sodium ferric gluconate complex', 'Moexipril', 'Moderate', 'Limited data suggest that ACE inhibitors may increase the risk and/or severity of systemic adverse effects associated with parenteral administration of iron. The exact mechanism of interaction is unclear. Certain systemic reactions stemming from intravenous iron therapy are thought to be mediated by inflammatory substances such as bradykinin in response to iron-catalyzed generation of toxic free radicals. Since ACE inhibitors decrease the breakdown of kinins, it is conceivable that they may potentiate these reactions.', 'DDInter', 1767317476, 'No specific intervention is necessary to avert a potential interaction. However, patients should be closely monitored during and after parenteral iron treatment.', 'Others', 'No specific intervention is necessary to avert a potential interaction.', NULL, NULL, 'Moderate', 'pharmacodynamic');
