INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1447792, 'Molindone', 'Metoclopramide', 'Major', 'Coadministration of metoclopramide with phenothiazines, neuroleptics, or other antidopaminergic agents (e.g., tetrabenazine) may increase the frequency and severity of extrapyramidal reactions (i.e., acute dystonic reactions, tardive dyskinesia, akathisia, Parkinson-like symptoms) due to additive antidopaminergic effects.', 'DDInter', 1767317476, 'Due to the potential for increased risk of serious and potentially irreversible extrapyramidal reactions, metoclopramide should not be prescribed in combination with other antidopaminergic agents. In addition, metoclopramide should not be used for longer than 12 weeks except in rare cases where therapeutic benefit is anticipated to outweigh the risk of developing tardive dyskinesia.', 'Synergism', 'Due to the potential for increased risk of serious and potentially irreversible extrapyramidal reactions, metoclopramide should not be prescribed in combination with other antidopaminergic agents.', NULL, NULL, 'Major', 'pharmacodynamic');
