INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1448076, 'Aminoglutethimide', 'Montelukast', 'Moderate', 'Coadministration with inducers of hepatic CYP450 isoenzymes may decrease the plasma concentrations of montelukast, which is metabolized by CYP450 2C8, 2C9, and 3A4 .', 'DDInter', 1767317476, 'No dosage adjustment for montelukast is recommended when used concomitantly with CYP450 inducers. However, clinical monitoring for potentially reduced therapeutic effects may be appropriate, especially for potent inducers such as carbamazepine, dexamethasone, enzalutamide, phenobarbital, phenytoin, rifamycins, and St. John''s wort.', 'Metabolism', 'No dosage adjustment for montelukast is recommended when used concomitantly with CYP450 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
