INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1448080, 'Etravirine', 'Montelukast', 'Moderate', 'Coadministration with inhibitors of CYP450 2C8 and/or 2C9 may increase the plasma concentrations of montelukast, which is metabolized by these isoenzymes in addition to CYP450 3A4. When montelukast was administered with the potent CYP450 2C8/2C9 inhibitor gemfibrozil, montelukast systemic exposure (AUC) increased by 4.4-fold.', 'DDInter', 1767317476, 'According to the product labeling, no dosage adjustment of montelukast is required when used in combination with gemfibrozil. However, it may be advisable to monitor patients for potentially increased adverse effects during coadministration with gemfibrozil or other CYP450 2C8/2C9 inhibitors.', 'Metabolism', 'According to the product labeling, no dosage adjustment of montelukast is required when used in combination with gemfibrozil.', NULL, NULL, 'Moderate', 'pharmacodynamic');
