INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1448190, 'Foscarnet', 'Moxifloxacin', 'Moderate', 'Limited data suggest that coadministration of foscarnet and ciprofloxacin may potentiate the risk of seizures. Both agents are individually epileptogenic and may have additive effects when combined. Specifically, foscarnet can decrease the seizure threshold by chelating ionized calcium and magnesium, while ciprofloxacin inhibits gamma-aminobutyric acid (GABA).', 'DDInter', 1767317476, 'Caution is advised during coadministration of foscarnet and ciprofloxacin, particularly in elderly patients and those with renal impairment, electrolyte and metabolic abnormalities, or underlying neurologic disorders. The same precaution may be applicable during therapy with other quinolones, although clinical data are lacking.', 'Synergism', 'Caution is advised during coadministration of foscarnet and ciprofloxacin, particularly in elderly patients and those with renal impairment, electrolyte and metabolic abnormalities, or underlying neurologic disorders.', NULL, NULL, 'Moderate', 'pharmacodynamic');
