        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1448438, 'Ethotoin', 'Mycophenolate mofetil', 'Minor', 'In vitro studies have shown that mycophenolic acid in concentrations up to 100 mcg/mL decreases protein binding of phenytoin from 90% to 87%. The clinical significance of this interaction is unknown. Decreased protein binding theoretically could lead to increased plasma phenytoin concentrations. However, clinical toxicity has not been reported. It may be appropriate to monitor more closely patients receiving phenytoin or other hydantoins with mycophenolic acid products.', NULL, '-', NULL, '-', 'Distribution', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
