INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1448583, 'Ganciclovir', 'Mycophenolic acid', 'Moderate', 'Data from a single-dose study do not suggest the potential for a clinically significant pharmacokinetic interaction between mycophenolic acid and ganciclovir in patients with normal renal function. However, since ganciclovir and the glucuronide metabolite of mycophenolic acid are both eliminated primarily by the kidney, plasma levels of both may increase in renal impairment due to competition for renal tubular secretion. In addition, use of mycophenolic acid and ganciclovir may increase the risk and severity of hematologic toxicity due to additive myelosuppressive effects.', 'DDInter', 1767317476, 'Caution is advised if ganciclovir or its prodrug, valganciclovir, must be used concomitantly with mycophenolic acid products in patients with impaired renal function. Close monitoring for toxicities such as myelo- and immunosuppression is recommended.', 'Synergism', 'Caution is advised if ganciclovir or its prodrug, valganciclovir, must be used concomitantly with mycophenolic acid products in patients with impaired renal function.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1448584, 'Gatifloxacin', 'Mycophenolic acid', 'Moderate', 'Theoretically, drugs that alter the gastrointestinal flora (e.g., antibiotics) may reduce the oral bioavailability of mycophenolic acid products. Some investigators believe that antimicrobials may interfere with the enterohepatic recirculation of mycophenolic acid by decreasing bacterial hydrolytic enzymes in the gastrointestinal tract that are responsible for regenerating mycophenolic acid from its glucuronide metabolite following first-pass metabolism.', 'DDInter', 1767317476, 'Close clinical and laboratory monitoring for evidence of diminished immunosuppressive effect of mycophenolic acid is recommended during concomitant therapy with anti-infective agents.', 'Absorption', 'Close clinical and laboratory monitoring for evidence of diminished immunosuppressive effect of mycophenolic acid is recommended during concomitant therapy with anti-infective agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
