INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1449086, 'Calcium citrate', 'Nadolol', 'Moderate', 'INTERVAL: Concurrent administration with calcium salts may decrease the oral bioavailability of atenolol and possibly other beta-blockers. The exact mechanism of interaction is unknown. The investigators suggest that prolongation of the elimination half-life induced by calcium coadministration may have led to atenolol cumulation during long-term dosing, which compensated for the reduced bioavailability.', 'DDInter', 1767317476, 'It may help to separate the administration times of beta-blockers and calcium products by at least 2 hours. Patients should be monitored for potentially diminished beta-blocking effects following the addition of calcium therapy.', 'Absorption', 'It may help to separate the administration times of beta-blockers and calcium products by at least 2 hours.', NULL, NULL, 'Moderate', 'pharmacodynamic');
