INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1449093, 'Diflunisal', 'Nadolol', 'Minor', 'High doses of salicylates may blunt the antihypertensive effects of beta-blockers. The proposed mechanism is inhibition of prostaglandin synthesis. Low-dose aspirin does not appear to affect blood pressure. In addition, beta-blockers may exert an antiplatelet effect, which may be additive with the effects of some salicylates. Metoprolol may also increase aspirin absorption and/or plasma concentrations of salicylates; however, the clinical significance of this effect is unknown. Data have been conflicting. Until more information is available, patients who require concomitant therapy should be monitored for altered antihypertensive response whenever a salicylate is introduced or discontinued, or when its dosage is modified.', 'DDInter', 1767317476, '-', 'Antagonism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
