INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1449513, 'Pitolisant', 'Naldemedine', 'Moderate', 'Coadministration with pitolisant may decrease the plasma concentrations of drugs that are primarily metabolized by CYP450 3A4. Pitolisant is a borderline/weak inducer of CYP450 3A4.', 'DDInter', 1767317476, 'Caution is advised when pitolisant is used in combination with sensitive CYP450 3A4 substrates. Clinical and laboratory monitoring may be appropriate whenever pitolisant is added to or withdrawn from therapy, and dosage adjustments made if necessary. It may be advisable to avoid concomitant use of pitolisant and CYP450 3A4 substrates with a narrow therapeutic index, if possible.', 'Metabolism', 'Caution is advised when pitolisant is used in combination with sensitive CYP450 3A4 substrates.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1449514, 'Ponatinib', 'Naldemedine', 'Moderate', 'Based on in vitro inhibition data, coadministration with ponatinib may increase the plasma concentrations of drugs that are substrates of the P-glycoprotein (P-gp) transporter. The proposed mechanism is decreased clearance in the intestine, liver, and/or kidney due to inhibition of P-glycoprotein-mediated drug efflux by ponatinib.', 'DDInter', 1767317476, 'Caution is advised when ponatinib is used concurrently with drugs that are known P-gp substrates, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever ponatinib is added to or withdrawn from therapy.', 'Others', 'Caution is advised when ponatinib is used concurrently with drugs that are known P-gp substrates, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
