INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1449990, 'Teriflunomide', 'Ketoprofen', 'Major', 'The recent, concomitant, or subsequent use (without the recommended leflunomide washout period or procedure) of other agents known to induce hepatotoxicity including NSAIDs and salicylates may potentiate the risk of liver injury associated with leflunomide. The risk is thought to extend to teriflunomide, its principal active metabolite, because recommended dosages of both result in a similar range of plasma concentrations of teriflunomide. Pharmacokinetically, the active metabolite of leflunomide has been shown to cause increases of 13% to 50% in the free fraction of diclofenac and ibuprofen. Additionally, in vitro studies indicate that the metabolite inhibits hepatic microsomal enzyme CYP450 2C9, which participates in the metabolism of many NSAIDs.', NULL, 'Patients receiving leflunomide or teriflunomide in combination with NSAIDs should be closely monitored for hepatotoxicity.', NULL, 'Patients receiving leflunomide or teriflunomide in combination with NSAIDs should be closely monitored for hepatotoxicity. Liver enzymes and bilirubin should be measured prior to initiation of leflunomide/teriflunomide therapy and at least monthly for the first six months of treatment and every 6 to 8 weeks thereafter. Patients with preexisting liver disease or elevated baseline liver enzymes (i.e., ALT greater than two times the upper limit of normal) should not receive leflunomide or teriflunomide. Patients who develop elevated serum ALT greater than three times ULN while receiving these medications should discontinue treatment and be given washout procedures with cholestyramine or activated charcoal to accelerate elimination of leflunomide''s active metabolite from plasma, which otherwise may take up to two years.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
