INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1450069, 'Metreleptin', 'Levonorgestrel', 'Moderate', 'Treatment with metreleptin, a recombinant analog of human leptin, may affect the pharmacokinetics of drugs that are primarily metabolized by CYP450 isoenzymes.', 'DDInter', 1767317476, 'Caution is advised when metreleptin is prescribed to patients receiving drugs with a narrow therapeutic index that are substrates of CYP450 isoenzymes, such as macrolide immunosuppressants, oral contraceptives, cyclosporine, theophylline, and warfarin. Clinical and laboratory monitoring are recommended following the initiation, discontinuation or change of dosage of metreleptin, and the individual dosage of the concomitant agents adjusted as needed.', 'Metabolism', 'Caution is advised when metreleptin is prescribed to patients receiving drugs with a narrow therapeutic index that are substrates of CYP450 isoenzymes, such as macrolide immunosuppressants, oral contraceptives, cyclosporine, theophylline, and warfarin.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1450070, 'Miconazole', 'Levonorgestrel', 'Moderate', 'Azole antifungal agents may increase the plasma concentrations of estrogens and progestins. The mechanism is decreased clearance of the hormones due to inhibition of CYP450 3A4 activity by azole antifungals.', 'DDInter', 1767317476, 'During concomitant therapy with azole antifungal agents, patients should be observed for increased or altered pharmacologic response to estrogens and progestins, and dosage(s) adjusted accordingly as necessary.', 'Metabolism', 'During concomitant therapy with azole antifungal agents, patients should be observed for increased or altered pharmacologic response to estrogens and progestins, and dosage(s) adjusted accordingly as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1450071, 'Mifepristone', 'Levonorgestrel', 'Moderate', 'Mifepristone is a progesterone-receptor antagonist and will interfere with the effectiveness of hormonal contraceptives.', 'DDInter', 1767317476, 'When mifepristone is prescribed daily to control hyperglycemia secondary to hypercortisolism in patients with endogenous Cushing''s syndrome, only nonhormonal contraceptive methods should be used. Because mifepristone is eliminated slowly from the body, drug interactions may be observed for a prolonged period following discontinuation (approximately 2 to 3 weeks if mifepristone had been administered chronically to steady state).', 'Antagonism', 'When mifepristone is prescribed daily to control hyperglycemia secondary to hypercortisolism in patients with endogenous Cushing''s syndrome, only nonhormonal contraceptive methods should be used.', NULL, NULL, 'Moderate', 'pharmacodynamic');
