        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1450300, 'Propafenone', 'Labetalol', 'Moderate', 'Propafenone may significantly increase serum levels and effects of some oral beta-blockers. The proposed mechanism is inhibition of CYP450 2D6 first-pass metabolism and decreased hepatic clearance. Data are available for metoprolol and propranolol only; however, other hepatically metabolized oral beta-blockers may also be affected. Renally excreted beta-blockers such as atenolol, carteolol, nadolol, or sotalol are not expected to interact. In addition, negative inotropic effects may be potentiated.', NULL, 'Patients receiving this combination should be monitored for hypotension, heart failure, bradycardia, arrhythmias, and mental status changes.', NULL, 'Patients receiving this combination should be monitored for hypotension, heart failure, bradycardia, arrhythmias, and mental status changes. Beta-blocker dosage may be decreased if necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
