        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1450612, 'Copper oxodotreotide Cu-64', 'Lanreotide', 'Major', 'INTERVAL: Coadministration of copper Cu 64 dotatate with non-radioactive somatostatin analogs may diminish the diagnostic effects of copper Cu 64 dotatate positron emission tomography (PET) imaging. Somatostatin analogs competitively bind to the same somatostatin receptor as copper Cu 64 dotatate, which may prevent the uptake of copper Cu 64 dotatate into tissues and affect the diagnostic imaging results.', NULL, 'PET imaging with copper Cu 64 dotatate should be performed prior to dosing with somatostatin analogs.', NULL, 'PET imaging with copper Cu 64 dotatate should be performed prior to dosing with somatostatin analogs. For patients receiving short-acting somatostatin analogs, a washout period of 2 days is recommended prior to imaging. For patients receiving long-acting somatostatin analogs, a washout period of 28 days is recommended prior to imaging.', 'Distribution', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
