        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1450895, 'Halofantrine', 'Lapatinib', 'Major', 'Halofantrine can cause dose-related prolongation of the QT interval at recommended therapeutic doses. QTc interval prolongation and death have been reported during combination use of halofantrine and mefloquine. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Coadministration of halofantrine with other drugs that can prolong the QT interval is considered contraindicated.', NULL, 'Coadministration of halofantrine with other drugs that can prolong the QT interval is considered contraindicated.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
INSERT OR IGNORE INTO drug_interactions (
