    ) VALUES (1451093, 'Tazemetostat', 'Lapatinib', 'Moderate', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of lapatinib, which is primarily metabolized by the isoenzyme.', NULL, 'The use of lapatinib in combination with potent CYP450 3A4 inducers such as carbamazepine, dexamethasone, phenobarbital, phenytoin, rifamycins, and St.', NULL, 'The use of lapatinib in combination with potent CYP450 3A4 inducers such as carbamazepine, dexamethasone, phenobarbital, phenytoin, rifamycins, and St. John''s wort should generally be avoided.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
