INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1451379, 'Troglitazone', 'Larotrectinib', 'Moderate', 'Coadministration with inducers of CYP450 3A4 and/or P-glycoprotein (P-gp) may decrease the plasma concentrations of larotrectinib, which is primarily metabolized by the isoenzyme as well as the efflux transporter.', 'DDInter', 1767317476, 'The potential for diminished pharmacologic effects of larotrectinib should be considered during coadministration with CYP450 3A4 and P-gp inducers. Some authorities advise that concomitant use of larotrectinib with moderate CYP450 3A4 and/or P-gp inducers should be avoided (UK). Alternative treatments may be required if an interaction is suspected.', 'Metabolism', 'The potential for diminished pharmacologic effects of larotrectinib should be considered during coadministration with CYP450 3A4 and P-gp inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1451380, 'Troleandomycin', 'Larotrectinib', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of larotrectinib, which is primarily metabolized by the isoenzyme. Increased exposure to larotrectinib may increase the risk of adverse effects such as neurotoxicity (delirium, dysarthria, dizziness, gait disturbance, paresthesia, encephalopathy, memory impairment, tremor) and hepatotoxicity (elevations in liver transaminases).', 'DDInter', 1767317476, 'Concomitant use of larotrectinib with potent CYP450 3A4 inhibitors should generally be avoided. If coadministration is required, the manufacturer recommends reducing the larotrectinib dose by 50%. After the inhibitor has been discontinued for 3 to 5 elimination half-lives, the larotrectinib dose that was taken prior to initiating the inhibitor may be resumed.', 'Metabolism', 'Concomitant use of larotrectinib with potent CYP450 3A4 inhibitors should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
