INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1451404, 'Zolpidem', 'Larotrectinib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of zolpidem, which is partially metabolized by the isoenzyme.', 'DDInter', 1767317476, 'Caution is advised when zolpidem is used with CYP450 3A4 inhibitors. Patients should be advised to avoid driving, operating machinery, or engaging in potentially hazardous activities requiring mental alertness and motor coordination until they know how the medications affect them, and to notify their physician if they experience excessive somnolence or dizziness.', 'Metabolism', 'Caution is advised when zolpidem is used with CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1451405, 'Zonisamide', 'Larotrectinib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of zonisamide, which is metabolized by the isoenzyme. While clinical data are lacking, the possibility of prolonged and/or increased pharmacologic effects of zonisamide should be considered.', 'DDInter', 1767317476, 'Dosage adjustments as well as clinical and laboratory monitoring may be appropriate whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy. Patients should be advised to notify their physician if they experience symptoms such as severe drowsiness, confusion, loss of coordination, depression, aphasia, decreased sweating, fever, loss of seizure control, or slow pulse.', 'Metabolism', 'Dosage adjustments as well as clinical and laboratory monitoring may be appropriate whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1451406, 'Oxitriptan', 'Lasmiditan', 'Major', 'Coadministration of lasmiditan and serotonergic agents may increase the risk of serotonin syndrome. Lasmiditan binds to the 5-HT(1F) receptor, where it presumably exerts therapeutic effects via agonist actions.', 'DDInter', 1767317476, 'Caution is advised during coadministration of lasmiditan and agents that increase serotonin. Patients should be closely monitored for symptoms of the serotonin syndrome during treatment. Extra caution is advised when increasing the dosages of these agents. The potential risk for serotonin syndrome should be considered even when administering serotonergic agents sequentially, as some agents may demonstrate a prolonged elimination half-life.', 'Synergism', 'Caution is advised during coadministration of lasmiditan and agents that increase serotonin.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1451407, 'Ambenonium', 'Lasmiditan', 'Moderate', 'Coadministration of lasmiditan and agents that lower heart rate may increase the risk of bradycardia.', 'DDInter', 1767317476, 'Caution is advised during coadministration of lasmiditan and agents that lower heart rate if the estimated decrease in heart rate may present a clinical risk. Consider monitoring heart rate in patients for whom the estimated decrease in heart rate may not be tolerated.', 'Synergism', 'Caution is advised during coadministration of lasmiditan and agents that lower heart rate if the estimated decrease in heart rate may present a clinical risk.', NULL, NULL, 'Moderate', 'pharmacodynamic');
