INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1451956, 'Troleandomycin', 'Lefamulin', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 and/or P-gp may significantly increase the plasma concentrations of lefamulin, which is a substrate of both the isoenzyme and efflux transporter. Coadministration of intravenous lefamulin with oral ketoconazole increased mean lefamulin Cmax and AUC by 6% and 31%, respectively. Increased exposure to lefamulin may increase the risk of QT interval prolongation, which has been associated with ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 1767317476, 'Concomitant use of lefamulin with potent inhibitors of CYP450 3A4 and/or P-gp should generally be avoided.', 'Metabolism', 'Concomitant use of lefamulin with potent inhibitors of CYP450 3A4 and/or P-gp should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1451957, 'Tucatinib', 'Lefamulin', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 and/or P-gp may significantly increase the plasma concentrations of lefamulin, which is a substrate of both the isoenzyme and efflux transporter. Coadministration of intravenous lefamulin with oral ketoconazole increased mean lefamulin Cmax and AUC by 6% and 31%, respectively. Increased exposure to lefamulin may increase the risk of QT interval prolongation, which has been associated with ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 1767317476, 'Concomitant use of lefamulin with potent inhibitors of CYP450 3A4 and/or P-gp should generally be avoided.', 'Metabolism', 'Concomitant use of lefamulin with potent inhibitors of CYP450 3A4 and/or P-gp should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
