INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1452048, 'Glimepiride', 'Leflunomide', 'Moderate', 'The active metabolite of leflunomide, known as A771726 or M1, has been shown to inhibit CYP450 2C9 in vitro and may increase the plasma concentrations of drugs that are substrates of the isoenzyme, including many nonsteroidal anti-inflammatory drugs (NSAIDs), sulfonylureas, phenytoin, and warfarin.', 'DDInter', 1767317476, 'Caution is advised if leflunomide is coadministered with drugs that are metabolized by CYP450 2C9, such as phenytoin, warfarin, and sulfonylureas. Clinical and laboratory monitoring may be appropriate whenever leflunomide is added to or withdrawn from therapy, and the dosages adjusted as necessary.', 'Metabolism', 'Caution is advised if leflunomide is coadministered with drugs that are metabolized by CYP450 2C9, such as phenytoin, warfarin, and sulfonylureas.', NULL, NULL, 'Moderate', 'pharmacodynamic');
