        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1452224, 'Tolazamide', 'Leflunomide', 'Moderate', 'The active metabolite of leflunomide, known as A771726 or M1, has been shown to inhibit CYP450 2C9 in vitro and may increase the plasma concentrations of drugs that are substrates of the isoenzyme, including many nonsteroidal anti-inflammatory drugs (NSAIDs), sulfonylureas, phenytoin, and warfarin.', NULL, 'Caution is advised if leflunomide is coadministered with drugs that are metabolized by CYP450 2C9, such as phenytoin, warfarin, and sulfonylureas.', NULL, 'Caution is advised if leflunomide is coadministered with drugs that are metabolized by CYP450 2C9, such as phenytoin, warfarin, and sulfonylureas. Clinical and laboratory monitoring may be appropriate whenever leflunomide is added to or withdrawn from therapy, and the dosages adjusted as necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
