        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1452391, 'Mifepristone', 'Lemborexant', 'Moderate', 'Coadministration of mifepristone with a drug that is both a substrate as well as inducer of CYP450 3A4 may result in decreased plasma concentrations of mifepristone and increased plasma concentrations of the other drug. Mifepristone itself is a substrate and inhibitor of CYP450 3A4. Theoretically, mifepristone may inhibit metabolism of the coadministered drug while its own metabolism may be induced by the coadministered drug.', NULL, 'When a drug that is both a substrate as well as inducer of CYP450 3A4 is coadministered with mifepristone, the possibility of diminished therapeutic response to mifepristone should be considered.', NULL, 'When a drug that is both a substrate as well as inducer of CYP450 3A4 is coadministered with mifepristone, the possibility of diminished therapeutic response to mifepristone should be considered. Because mifepristone is eliminated slowly from the body, drug interactions may be observed for a prolonged period following discontinuation (approximately 2 to 3 weeks if mifepristone had been administered chronically to steady state).', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
