INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1452400, 'Nicotine', 'Lemborexant', 'Moderate', 'Coadministration with lemborexant may decrease the plasma concentrations and pharmacologic effects of CYP450 2B6 substrates. The proposed mechanism is lemborexant-mediated induction of the CYP450 2B6 isoenzyme.', 'DDInter', 1767317476, 'Caution is advised if lemborexant is used concomitantly with drugs that are substrates of CYP450 2B6. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever lemborexant is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if lemborexant is used concomitantly with drugs that are substrates of CYP450 2B6.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1452401, 'Nilotinib', 'Lemborexant', 'Major', 'Coadministration with weak inhibitors of CYP450 3A4 may increase the plasma concentrations of lemborexant, which is primarily metabolized by the isoenzyme. Concomitant use of a weak CYP450 3A4 inhibitor is predicted to increase lemborexant exposure by less than 2-fold. Increased exposure to lemborexant may increase the risk of adverse reactions such as central nervous system (CNS) depression, sleep paralysis, hallucinations, complex sleep behaviors, worsening of depression or suicidal ideation, nightmares, palpitations, or headache.', 'DDInter', 1767317476, 'The lemborexant dose should be limited to 5 mg no more than once per night when coadministered with weak CYP450 3A4 inhibitors.', 'Metabolism', 'The lemborexant dose should be limited to 5 mg no more than once per night when coadministered with weak CYP450 3A4 inhibitors.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1452402, 'Nitisinone', 'Lemborexant', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of and exposure to, nitisinone. The proposed mechanism is inhibition of CYP450 3A4-mediated metabolism of nitisinone, which has been shown in in vitro studies to be metabolized by this isoenzyme.', 'DDInter', 1767317476, 'Until more information is available, caution is advised if nitisinone is used concomitantly with CYP450 3A4 inhibitors. Pharmacologic response to nitisinone should be monitored whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the nitisinone dosage adjusted as necessary. Patients should be monitored for the development of adverse effects.', 'Metabolism', 'Until more information is available, caution is advised if nitisinone is used concomitantly with CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1452403, 'Norgestrel', 'Lemborexant', 'Moderate', 'RECOMMENDED: Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of estrogens and progestins. Estrogens have been shown in in vitro and in vivo studies to be partially metabolized by CYP450 3A4, and other steroids including progestins are also believed to undergo metabolism by this isoenzyme.', 'DDInter', 1767317476, 'Pharmacologic response to estrogens and progestins should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the hormone dosage adjusted as necessary. For patients receiving hormonal contraceptives, additional or alternative non-hormonal birth control may be advisable during concomitant therapy with CYP450 3A4 inducers. Additional or alternative non-hormonal birth control may be recommended beyond discontinuation of the CYP450 3A4 inducer(s).', 'Metabolism', 'Pharmacologic response to estrogens and progestins should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the hormone dosage adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
