INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1452461, 'Repaglinide', 'Lemborexant', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of repaglinide, which is metabolized by the isoenzyme in the intestine and liver.', 'DDInter', 1767317476, 'Because the antidiabetic effect of repaglinide is dose- and concentration-dependent, patients treated concomitantly with rifampin may require a higher dosage of repaglinide. Blood glucose should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy in patients stabilized on their existing antidiabetic regimen, and the repaglinide dosage adjusted as necessary. Patients should be advised to contact their physician if they experience loss of glycemic control.', 'Metabolism', 'Because the antidiabetic effect of repaglinide is dose- and concentration-dependent, patients treated concomitantly with rifampin may require a higher dosage of repaglinide.', NULL, NULL, 'Moderate', 'pharmacodynamic');
