INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1452473, 'Sarecycline', 'Lemborexant', 'Moderate', 'Coadministration with sarecycline may increase the plasma concentrations of drugs that are substrates of the P-glycoprotein (P-gp) transporter. The proposed mechanism is decreased clearance in the intestine, kidney, and/or liver due to inhibition of P-gp-mediated efflux by sarecycline.', 'DDInter', 1767317476, 'Caution is advised when sarecycline is used concurrently with drugs that are P-gp substrates, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever sarecycline is added to or withdrawn from therapy.', 'Others', 'Caution is advised when sarecycline is used concurrently with drugs that are P-gp substrates, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
