INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1452477, 'Selumetinib', 'Lemborexant', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of selumetinib, which is primarily metabolized by CYP450 3A4 and to a lesser extent by CYP450 2C19, 1A2, 2C9, 2E1, and 3A5. Selumetinib also undergoes glucuronidation by UGT1A1 and UGT1A3. Increased exposures to selumetinib and N-desmethyl selumetinib may increase the risk and/or severity of serious adverse effects such as cardiomyopathy (decrease in left ventricular ejection fraction by 10% or more below baseline), ocular toxicity (blurred vision, photophobia, cataracts, ocular hypertension, retinal pigment epithelial detachment, retinal vein occlusion), gastrointestinal toxicity (diarrhea, colitis), skin toxicity (dermatitis acneiform, maculopapular rash, eczema), and musculoskeletal toxicity (creatine phosphokinase elevations, myalgia, rhabdomyolysis).', 'DDInter', 1767317476, 'Caution is advised when selumetinib is coadministered with CYP450 3A4 inhibitors. Dosage adjustments may be required based on clinical response and tolerance. Please refer to the product labeling for specific guidelines on dosing adjustments.', 'Metabolism', 'Caution is advised when selumetinib is coadministered with CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
