INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1452853, 'Methylergometrine', 'Letermovir', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of ergot derivatives, which are primarily metabolized by the isoenzyme. The interaction has occurred in patients receiving ergotamine or dihydroergotamine with potent CYP450 3A4 inhibitors such as macrolide antibiotics and protease inhibitors.', 'DDInter', 1767317476, 'Given the potential for ergot toxicity characterized by peripheral vasospasm, ischemia, thrombosis, tachycardia and hypertension, concomitant use of ergot derivatives with potent CYP450 3A4 inhibitors is considered contraindicated.', 'Metabolism', 'Given the potential for ergot toxicity characterized by peripheral vasospasm, ischemia, thrombosis, tachycardia and hypertension, concomitant use of ergot derivatives with potent CYP450 3A4 inhibitors is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
