INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1452947, 'Thioridazine', 'Letermovir', 'Moderate', 'Coadministration with inducers of the P-glycoprotein (P-gp) efflux transporter and/or the uridine diphosphate glucuronosyltransferase (UGT) enzymes may decrease the plasma concentrations of letermovir according to the product labeling. Because letermovir is a substrate of P-gp and UGT 1A1 and 1A3, induction of the efflux transporter and metabolic enzymes may theoretically increase the clearance of letermovir.', 'DDInter', 1767317476, 'Due to the potential loss of efficacy, use of letermovir with inducers of the P-glycoprotein (P-gp) efflux transporter and/or the uridine diphosphate glucuronosyltransferase (UGT) enzymes is not recommended.', 'Metabolism', 'Due to the potential loss of efficacy, use of letermovir with inducers of the P-glycoprotein (P-gp) efflux transporter and/or the uridine diphosphate glucuronosyltransferase (UGT) enzymes is not recommended.', NULL, NULL, 'Moderate', 'pharmacodynamic');
