INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1452966, 'Venlafaxine', 'Letermovir', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of venlafaxine and its metabolite, O-desmethylvenlafaxine (ODV). The possibility of prolonged and/or increased pharmacologic effects of venlafaxine should be considered. Although venlafaxine is primarily metabolized by CYP450 2D6, there is some evidence that CYP450 3A4 may play a minor role, thus any alteration in its activity levels could conceivably affect the metabolism of venlafaxine. The interaction may be of greater concern in patients who are CYP450 2D6-deficient, or so-called poor metabolizers of CYP450 2D6 (approximately 7% of Caucasians and less than 2% of Asians and individuals of African descent).', 'DDInter', 1767317476, 'Caution is recommended when venlafaxine is used with CYP450 3A4 inhibitors.', 'Metabolism', 'Caution is recommended when venlafaxine is used with CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
