INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1453253, 'Bismuth subsalicylate', 'Levamlodipine', 'Moderate', 'Limited data indicate that some cyclooxygenase inhibitors may attenuate the antihypertensive effects of some calcium channel blockers. The mechanism appears to be related to an alteration of vascular tone, which is dependent on prostacyclins and other vasodilatory prostanoids. When a nonsteroidal anti-inflammatory drug (NSAID) is added to the regimen of a patient who is already taking a calcium channel blocker, increased blood pressure may result. Also, the clinician should be aware that the risk of hypotension is increased when NSAIDs are withdrawn from the regimen.', 'DDInter', 1767317476, 'Monitoring for altered blood pressure control is recommended.', 'Antagonism', 'Monitoring for altered blood pressure control is recommended.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1453254, 'Boceprevir', 'Levamlodipine', 'Moderate', 'Coadministration with the hepatitis C virus (HCV) NS3/4A protease inhibitors, boceprevir and telaprevir, may increase the plasma concentrations and pharmacologic effects of calcium channel blockers, especially the dihydropyridines (e.g., amlodipine, felodipine, nicardipine, nifedipine, nisoldipine). The mechanism involves inhibition of intestinal and hepatic CYP450 3A4, the isoenzyme primarily responsible for the metabolic clearance of most calcium channel blockers.', 'DDInter', 1767317476, 'Close monitoring of clinical response and tolerance is recommended if calcium channel blockers are used in combination with boceprevir or telaprevir. Dosage reduction may be required for the calcium channel blocker, particularly if it is a dihydropyridine. Patients should be advised to seek medical attention if they experience edema or swelling of the lower extremities; sudden, unexplained weight gain; difficulty breathing; chest pain or tightness; or hypotension as indicated by dizziness, fainting, or orthostasis.', 'Metabolism', 'Close monitoring of clinical response and tolerance is recommended if calcium channel blockers are used in combination with boceprevir or telaprevir.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1453255, 'Bromfenac', 'Levamlodipine', 'Moderate', 'Limited data indicate that some cyclooxygenase inhibitors may attenuate the antihypertensive effects of some calcium channel blockers. The mechanism appears to be related to an alteration of vascular tone, which is dependent on prostacyclins and other vasodilatory prostanoids. When a nonsteroidal anti-inflammatory drug (NSAID) is added to the regimen of a patient who is already taking a calcium channel blocker, increased blood pressure may result. Also, the clinician should be aware that the risk of hypotension is increased when NSAIDs are withdrawn from the regimen.', 'DDInter', 1767317476, 'Monitoring for altered blood pressure control is recommended.', 'Antagonism', 'Monitoring for altered blood pressure control is recommended.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1453256, 'Butabarbital', 'Levamlodipine', 'Moderate', 'Barbiturates may increase the hepatic metabolism of some dihydropyridine calcium channel blockers by inducing CYP450 3A4 hepatic metabolism. The serum concentrations and effects of these calcium channel blockers may be significantly decreased.', 'DDInter', 1767317476, 'If concurrent administration cannot be avoided, the patient should be closely monitored for changes in pharmacologic effects whenever a barbiturate is initiated, discontinued or its dosage adjusted. An increased dosage of the calcium channel blocker may be necessary.', 'Metabolism', 'If concurrent administration cannot be avoided, the patient should be closely monitored for changes in pharmacologic effects whenever a barbiturate is initiated, discontinued or its dosage adjusted.', NULL, NULL, 'Moderate', 'pharmacodynamic');
