    ) VALUES (1453562, 'Methacholine', 'Levobunolol (ophthalmic)', 'Major', 'Patients who are concurrently receiving beta blockers may have exaggerated or prolonged responses (i.e., bronchoconstriction) to methacholine challenge and may not respond readily to treatment. Beta-2-adrenergic blockade increases airway resistance. Ophthalmically applied beta blockers undergo some systemic absorption and potentially may also interact.', NULL, 'Administration of methacholine to a patient on any beta blocker therapy is considered contraindicated.', NULL, 'Administration of methacholine to a patient on any beta blocker therapy is considered contraindicated.', 'Others', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
