INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1453858, 'Iohexol', 'Levofloxacin', 'Major', 'Intrathecal administration of iodinated contrast media may induce seizures. Although clinical data are generally lacking, there may be a theoretical risk of increased seizure potential when used with selective serotonin reuptake inhibitors (SSRI antidepressants or anorectics), monoamine oxidase inhibitors, neuroleptic agents, central nervous system stimulants, opioids, tricyclic antidepressants, other tricyclic compounds (e.g., cyclobenzaprine, phenothiazines), and/or any substance that can reduce the seizure threshold (e.g., carbapenems, cholinergic agents, fluoroquinolones, interferons, chloroquine, mefloquine, lindane, theophylline). These agents are often individually epileptogenic and may have additive effects when combined.', 'DDInter', 1767317476, 'Drugs that can lower the seizure threshold should preferably be withheld for at least 48 hours prior to and 24 hours following intrathecal administration of iodinated contrast media, provided that temporary interruption of therapy does not pose an undue risk to the patient. Otherwise, close monitoring is advised during and after contrast administration.', 'Synergism', 'Drugs that can lower the seizure threshold should preferably be withheld for at least 48 hours prior to and 24 hours following intrathecal administration of iodinated contrast media, provided that temporary interruption of therapy does not pose an undue risk to the patient.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1453859, 'Iron protein succinylate', 'Levofloxacin', 'Moderate', 'INTERVAL: Oral preparations that contain magnesium, aluminum, or calcium may significantly decrease the gastrointestinal absorption of quinolone antibiotics. Absorption may also be reduced by sucralfate, which contains aluminum, as well as other polyvalent cations such as iron and zinc. The mechanism is chelation of quinolones by polyvalent cations, forming a complex that is poorly absorbed from the gastrointestinal tract.', 'DDInter', 1767317476, 'When coadministration cannot be avoided, quinolone antibiotics should be dosed either 2 to 4 hours before or 4 to 6 hours after polyvalent cation-containing products to minimize the potential for interaction.', 'Absorption', 'When coadministration cannot be avoided, quinolone antibiotics should be dosed either 2 to 4 hours before or 4 to 6 hours after polyvalent cation-containing products to minimize the potential for interaction.', NULL, NULL, 'Moderate', 'pharmacodynamic');
