    ) VALUES (1454667, 'Rifampicin', 'Linezolid', 'Moderate', 'Coadministration with rifampin may decrease the plasma concentrations of linezolid. The mechanism of interaction is unknown.', NULL, 'The potential for diminished pharmacologic effects of linezolid should be considered during coadministration with rifampin.', NULL, 'The potential for diminished pharmacologic effects of linezolid should be considered during coadministration with rifampin.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
