INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1454972, 'Sibutramine', 'Lisdexamfetamine', 'Major', 'Sibutramine is a potent inhibitor of norepinephrine and serotonin reuptake, and a less potent inhibitor of dopamine reuptake. As such, it may potentiate the pharmacologic response to sympathomimetic agents due to additive or synergistic effects. Although the interaction has not been studied with sibutramine, it has been reported with the selective serotonin reuptake inhibitor, fluoxetine.', 'DDInter', 1767317476, 'The use of sibutramine in combination with amphetamines and other centrally-acting appetite suppressants is considered contraindicated.', 'Synergism', 'The use of sibutramine in combination with amphetamines and other centrally-acting appetite suppressants is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1454973, 'Siponimod', 'Lisdexamfetamine', 'Major', 'Due to its significant bradycardic effects, the risk of QT prolongation and torsade de pointes arrhythmia may be increased during initiation of siponimod treatment in patients receiving drugs that prolong the QT interval.', 'DDInter', 1767317476, 'Siponimod has not been studied in patients receiving drugs that can prolong the QT interval. Because bradycardia and AV block are recognized risk factors for QT prolongation and torsade de pointes arrhythmia, treatment with siponimod should generally not be initiated in patients who are concurrently treated with QT prolonging drugs with known arrhythmogenic properties.', 'Synergism', 'Siponimod has not been studied in patients receiving drugs that can prolong the QT interval.', NULL, NULL, 'Major', 'pharmacodynamic');
