INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1454977, 'Solriamfetol', 'Lisdexamfetamine', 'Moderate', 'Additive or synergistic effects on blood pressure and/or heart rate may occur when solriamfetol is combined with other drugs that increase blood pressure and/or heart rate. The use of solriamfetol alone has been associated with dose-dependent increases in blood pressure and heart rate.', 'DDInter', 1767317476, 'Caution is advised if solriamfetol is used with other drugs that increase blood pressure and/or heart rate. Blood pressure should be monitored prior to initiating solriamfetol therapy and regularly during treatment. In addition, hypertension should be controlled prior to treatment, and any new-onset hypertension or exacerbations of pre-existing hypertension that occur during solriamfetol therapy should be treated. Discontinuation of solriamfetol should be considered in patients who experience an increase in blood pressure or heart rate that cannot be managed with dose reduction of solriamfetol or other appropriate medical intervention.', 'Synergism', 'Caution is advised if solriamfetol is used with other drugs that increase blood pressure and/or heart rate.', NULL, NULL, 'Moderate', 'pharmacodynamic');
