INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1455026, 'Enoxaparin', 'Lisinopril', 'Moderate', 'The concomitant use of angiotensin converting enzyme (ACE) inhibitors or angiotensin receptor blockers (ARBs) and heparin or low molecular weight heparins may increase the risk of hyperkalemia. ACE inhibitors, ARBs, and some heparins individually have been associated with increased potassium levels or hyperkalemia.', 'DDInter', 1767317476, 'Caution is advised if ACE inhibitors or ARBs are used with heparin or low molecular weight heparin. Serum potassium and renal function should be checked regularly.', 'Synergism', 'Caution is advised if ACE inhibitors or ARBs are used with heparin or low molecular weight heparin.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1455027, 'Epirubicin', 'Lisinopril', 'Moderate', 'The concomitant or sequential use of epirubicin with agents known to induce hepatotoxicity may potentiate the risk of liver injury. Epirubicin undergoes elimination through the hepatobiliary system.', 'DDInter', 1767317476, 'Caution and clinical and laboratory monitoring for signs of liver injury are advisable before, during, or after use of epirubicin with other agents that are potentially hepatotoxic.', 'Synergism', 'Caution and clinical and laboratory monitoring for signs of liver injury are advisable before, during, or after use of epirubicin with other agents that are potentially hepatotoxic.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1455028, 'Eplerenone', 'Lisinopril', 'Moderate', 'Coadministration of eplerenone with ACE inhibitors or angiotensin II receptor antagonists may increase the risk of hyperkalemia due to additive pharmacodynamic effects.', 'DDInter', 1767317476, 'Given the potential for serious, sometimes fatal cardiac arrhythmias associated with hyperkalemia, caution is advised if eplerenone is administered with ACE inhibitors or angiotensin II receptor antagonists. Periodic monitoring of serum potassium levels is recommended until the effect of eplerenone is established. In clinical trials, monitoring occurred every 2 weeks for the first 1 to 2 months, then monthly thereafter. Particular caution is warranted in patients with renal impairment, diabetes, old age, or dehydration. Patients should be advised to seek medical attention if they experience signs and symptoms of hyperkalemia such as nausea, vomiting, weakness, listlessness, tingling of the extremities, paralysis, confusion, weak pulse, and a slow or irregular heartbeat.', 'Synergism', 'Given the potential for serious, sometimes fatal cardiac arrhythmias associated with hyperkalemia, caution is advised if eplerenone is administered with ACE inhibitors or angiotensin II receptor antagonists.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1455029, 'Erythropoietin', 'Lisinopril', 'Minor', 'Angiotensin-converting enzyme (ACE) inhibitors may interfere with the effects of both endogenous and exogenous erythropoietin. Until further data are available, it may be appropriate to monitor patients for potentially diminished hematopoietic response to recombinant erythropoietin therapy following the addition of an ACE inhibitor. It is not known if other erythropoiesis-stimulating agents are similarly affected.', 'DDInter', 1767317476, '-', 'Antagonism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1455030, 'Epoprostenol', 'Lisinopril', 'Moderate', 'Coadministration of medicines targeting the prostacyclin pathway (e.g., prostacyclin (PGI2), PGI2 analogs, or selective non-prostanoid prostacyclin IP receptor agonists) with diuretics, antihypertensive agents, or other vasodilators may potentiate the hypotensive effects of these agents. The mechanism involves the additive reduction in blood pressure due to the vasodilatory effects of PGI2, PGI2 analogs or selective non-prostanoid prostacyclin IP receptor agonists on the prostacyclin pathway.', 'DDInter', 1767317476, 'While therapies that target the prostacyclin pathway have been used in combination with diuretics, antihypertensives, or other vasodilators in the management of pulmonary arterial hypertension, caution is recommended if they must be administered concurrently. If these drugs are used together, it is generally recommended that blood pressure be measured more frequently until a stable blood pressure pattern is observed. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their doctor if they experience dizziness, lightheadedness, syncope, orthostatic hypotension, or tachycardia.', 'Synergism', 'While therapies that target the prostacyclin pathway have been used in combination with diuretics, antihypertensives, or other vasodilators in the management of pulmonary arterial hypertension, caution is recommended if they must be administered concurrently.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1455031, 'Ertugliflozin', 'Lisinopril', 'Moderate', 'Sodium-glucose co-transporter 2 (SGLT-2) inhibitors may potentiate the hypotensive effects of diuretics and other antihypertensive agents or vasodilators. Inhibition of glucose and sodium co-transport produces mild diuresis and transient natriuresis, resulting in intravascular volume contraction. Volume depletion-related adverse reactions including hypotension, postural dizziness, orthostatic hypotension, syncope, and dehydration can occur after initiating treatment with SGLT-2 inhibitors, and the risk may be increased with concomitant use of other agents that can lower blood pressure.', 'DDInter', 1767317476, 'Caution is advised if SGLT-2 inhibitors are coadministered with diuretics and other hypotensive agents, particularly in the elderly and patients with impaired renal function. Prior to initiating SGLT-2 inhibitors, volume status should be assessed and corrected, if necessary. Clinical and laboratory monitoring are recommended during therapy, including electrolytes, fluid status, renal function, and blood pressure. If volume depletion occurs, treatment with SGLT-2 inhibitors should be interrupted until the condition is corrected.', 'Synergism', 'Caution is advised if SGLT-2 inhibitors are coadministered with diuretics and other hypotensive agents, particularly in the elderly and patients with impaired renal function.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1455032, 'Estramustine', 'Lisinopril', 'Moderate', 'The concomitant administration of estramustine and angiotensin enzyme converting (ACE) inhibitors may increase the risk of angioedema. The mechanism is unknown. Cases of angioedema have been associated with estramustine administration; many of these patients were also receiving ACE inhibitors.', 'DDInter', 1767317476, 'It is recommended to monitor patients for adverse reactions during concomitant therapy. Clinicians and patients should be aware of the potential for increased risk of angioedema when estramustine is prescribed with ACE inhibitors. Patients should be advised to promptly discontinue these medications and seek medical attention if they develop signs or symptoms suggestive of angioedema such as swelling of the face, extremities, eyes, lips, or tongue, and difficulty swallowing or breathing.', 'Others', 'It is recommended to monitor patients for adverse reactions during concomitant therapy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1455033, 'Etacrynic acid', 'Lisinopril', 'Moderate', 'Although they are frequently combined in clinical practice, diuretics and angiotensin converting enzyme (ACE) inhibitors may have additive effects. Coadministration makes hypotension and hypovolemia more likely than does either drug alone. Some ACE inhibitors may attenuate the increase in the urinary excretion of sodium caused by some loop diuretics. Some patients on diuretics, especially those on dialysis or a dietary salt restriction, may experience acute hypotension with lightheadedness and dizziness after receiving the first dose of the ACE inhibitor. In addition, ACE inhibitors may cause renal insufficiency or acute renal failure in patients with sodium depletion or renal artery stenosis.', 'DDInter', 1767317476, 'Monitoring of blood pressure, diuresis, electrolytes, and renal function is recommended during coadministration. The possibility of first-dose hypotensive effects may be minimized by initiating therapy with small doses of the ACE inhibitor, or either discontinuing the diuretic temporarily or increasing the salt intake approximately one week prior to initiating an ACE inhibitor. Alternatively, the patient may remain under medical supervision for at least two hours after the first dose of the ACE inhibitor, or until blood pressure has stabilized.', 'Synergism', 'Monitoring of blood pressure, diuresis, electrolytes, and renal function is recommended during coadministration.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1455034, 'Etodolac', 'Lisinopril', 'Moderate', 'Nonsteroidal anti-inflammatory drugs (NSAIDs) may attenuate the antihypertensive effects of ACE inhibitors. The proposed mechanism is NSAID-induced inhibition of renal prostaglandin synthesis, which results in unopposed pressor activity producing hypertension. In addition, NSAIDs can cause fluid retention, which also affects blood pressure. Concomitant use of NSAIDs and ACE inhibitors may also cause deterioration in renal function, particularly in patients who are elderly or volume-depleted (including those on diuretic therapy) or have compromised renal function. Acute renal failure may occur, although effects are usually reversible. Chronic use of NSAIDs alone may be associated with renal toxicities, including elevations in serum creatinine and BUN, tubular necrosis, glomerulitis, renal papillary necrosis, acute interstitial nephritis, nephrotic syndrome, and renal failure.', 'DDInter', 1767317476, 'Patients receiving ACE inhibitors who require prolonged (greater than 1 week) concomitant therapy with an NSAID should have blood pressure monitored more closely following initiation, discontinuation, or change of dosage of the NSAID. Renal function should also be evaluated periodically during prolonged coadministration. The interaction is not expected to occur with low doses (e.g., low-dose aspirin) or intermittent short-term administration of NSAIDs.', 'Antagonism', 'Patients receiving ACE inhibitors who require prolonged (greater than 1 week) concomitant therapy with an NSAID should have blood pressure monitored more closely following initiation, discontinuation, or change of dosage of the NSAID.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1455035, 'Everolimus', 'Lisinopril', 'Moderate', 'The use of mTOR (mammalian target of rapamycin) inhibitors has been associated with the development of angioedema, and coadministration with other drugs that are also known to cause angioedema such as ACE inhibitors, angiotensin II receptor antagonists, and renin inhibitors may increase the risk.', 'DDInter', 1767317476, 'Clinicians and patients should be aware of the potential for increased risk of angioedema when mTOR inhibitors are prescribed with other drugs that are also known to cause angioedema. Patients should be advised to promptly discontinue these medications and seek medical attention if they develop signs or symptoms suggestive of angioedema such as swelling of the face, extremities, eyes, lips, or tongue, and difficulty swallowing or breathing.', 'Synergism', 'Clinicians and patients should be aware of the potential for increased risk of angioedema when mTOR inhibitors are prescribed with other drugs that are also known to cause angioedema.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1455036, 'Exenatide', 'Lisinopril', 'Moderate', 'Drugs that affect renal function and/or hydration status may potentiate the adverse renal effects of exenatide. The mechanism of interaction is unknown. Exenatide has not been found to be directly nephrotoxic in preclinical or clinical studies.', 'DDInter', 1767317476, 'Clinicians should be aware of the potential for increased adverse renal effects of exenatide during coadministration with drugs that are known to affect renal function or hydration status.', 'Others', 'Clinicians should be aware of the potential for increased adverse renal effects of exenatide during coadministration with drugs that are known to affect renal function or hydration status.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1455037, 'Felodipine', 'Lisinopril', 'Minor', 'Calcium channel blockers and angiotensin converting enzyme (ACE) inhibitors may have additive hypotensive effects. While these drugs are often safely used together, careful monitoring of the systemic blood pressure is recommended during coadministration, especially during the first one to three weeks of therapy.', 'DDInter', 1767317476, '-', 'Synergism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1455038, 'Fenoldopam', 'Lisinopril', 'Moderate', 'The use of fenoldopam with other hypotensive agents has not been studied. However, these combinations would theoretically increase the risk of severe symptomatic hypotension.', 'DDInter', 1767317476, 'Caution is strongly advised during concurrent use. Inpatient monitoring of the heart rate and blood pressure is recommended. Sublingual nitroglycerin has been used safely with fenoldopam.', 'Synergism', 'Caution is strongly advised during concurrent use.', NULL, NULL, 'Moderate', 'pharmacodynamic');
