INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1455048, 'Garlic', 'Lisinopril', 'Minor', 'Garlic has been found in some studies to lower blood pressure and may theoretically potentiate the effects of hypotensive agents. A meta-analysis of 11 randomized, placebo-controlled studies showed a mean decrease of 4.6 mmHg for systolic blood pressure and no significant effect for diastolic blood pressure in subjects treated with garlic (most often garlic powder 600 to 900 mg/day, providing 3.6 to 5.4 mg of the active component allicin) compared to placebo.', 'DDInter', 1767317476, '-', 'Synergism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1455049, 'Glipizide', 'Lisinopril', 'Moderate', 'The hypoglycemic effect of insulin secretagogues (e.g., sulfonylureas, meglitinides) may be potentiated by certain drugs, including ACE inhibitors, 4-aminoquinolines, amylin analogs, anabolic steroids, fibrates, monoamine oxidase inhibitors (MAOIs, including linezolid), nonsteroidal anti-inflammatory drugs (NSAIDs), salicylates, selective serotonin reuptake inhibitors (SSRIs), sulfonamides, disopyramide, propoxyphene, quinine, quinidine, and ginseng. These drugs may increase the risk of hypoglycemia by enhancing insulin sensitivity (ACE inhibitors, fibrates, ginseng); stimulating insulin secretion (salicylates, NSAIDs, disopyramide, quinine, quinidine, MAOIs, ginseng); decreasing insulin clearance and resistance (4-aminoquinolines); increasing peripheral glucose utilization (SSRIs, insulin-like growth factor); inhibiting gluconeogenesis (SSRIs, MAOIs, insulin-like growth factor); slowing the rate of gastric emptying (amylin analogs); and/or suppressing postprandial glucagon secretion (amylin analogs). Or, they may increase plasma concentration of insulin secretagogues by displacing them from plasma protein binding sites and/or inhibiting their metabolism (fibrates, NSAIDs, salicylates, sulfonamides).', 'DDInter', 1767317476, 'Close monitoring for the development of hypoglycemia is recommended if these drugs are coadministered with insulin secretagogues, particularly in patients with advanced age and/or renal impairment. The oral antidiabetic dosage(s) may require adjustment if an interaction is suspected. Patients should be apprised of the signs and symptoms of hypoglycemia (e.g., headache, dizziness, drowsiness, nausea, hunger, tremor, weakness, sweating, palpitations), how to treat it, and to contact their doctor if it occurs. Patients should be observed for loss of glycemic control when these drugs are withdrawn.', 'Synergism', 'Close monitoring for the development of hypoglycemia is recommended if these drugs are coadministered with insulin secretagogues, particularly in patients with advanced age and/or renal impairment.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1455050, 'Glyburide', 'Lisinopril', 'Moderate', 'The hypoglycemic effect of insulin secretagogues (e.g., sulfonylureas, meglitinides) may be potentiated by certain drugs, including ACE inhibitors, 4-aminoquinolines, amylin analogs, anabolic steroids, fibrates, monoamine oxidase inhibitors (MAOIs, including linezolid), nonsteroidal anti-inflammatory drugs (NSAIDs), salicylates, selective serotonin reuptake inhibitors (SSRIs), sulfonamides, disopyramide, propoxyphene, quinine, quinidine, and ginseng. These drugs may increase the risk of hypoglycemia by enhancing insulin sensitivity (ACE inhibitors, fibrates, ginseng); stimulating insulin secretion (salicylates, NSAIDs, disopyramide, quinine, quinidine, MAOIs, ginseng); decreasing insulin clearance and resistance (4-aminoquinolines); increasing peripheral glucose utilization (SSRIs, insulin-like growth factor); inhibiting gluconeogenesis (SSRIs, MAOIs, insulin-like growth factor); slowing the rate of gastric emptying (amylin analogs); and/or suppressing postprandial glucagon secretion (amylin analogs). Or, they may increase plasma concentration of insulin secretagogues by displacing them from plasma protein binding sites and/or inhibiting their metabolism (fibrates, NSAIDs, salicylates, sulfonamides).', 'DDInter', 1767317476, 'Close monitoring for the development of hypoglycemia is recommended if these drugs are coadministered with insulin secretagogues, particularly in patients with advanced age and/or renal impairment. The oral antidiabetic dosage(s) may require adjustment if an interaction is suspected. Patients should be apprised of the signs and symptoms of hypoglycemia (e.g., headache, dizziness, drowsiness, nausea, hunger, tremor, weakness, sweating, palpitations), how to treat it, and to contact their doctor if it occurs. Patients should be observed for loss of glycemic control when these drugs are withdrawn.', 'Synergism', 'Close monitoring for the development of hypoglycemia is recommended if these drugs are coadministered with insulin secretagogues, particularly in patients with advanced age and/or renal impairment.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1455051, 'Glycerin', 'Lisinopril', 'Moderate', 'Although they are frequently combined in clinical practice, diuretics and angiotensin converting enzyme (ACE) inhibitors may have additive effects. Coadministration makes hypotension and hypovolemia more likely than does either drug alone. Some ACE inhibitors may attenuate the increase in the urinary excretion of sodium caused by some loop diuretics. Some patients on diuretics, especially those on dialysis or a dietary salt restriction, may experience acute hypotension with lightheadedness and dizziness after receiving the first dose of the ACE inhibitor. In addition, ACE inhibitors may cause renal insufficiency or acute renal failure in patients with sodium depletion or renal artery stenosis.', 'DDInter', 1767317476, 'Monitoring of blood pressure, diuresis, electrolytes, and renal function is recommended during coadministration. The possibility of first-dose hypotensive effects may be minimized by initiating therapy with small doses of the ACE inhibitor, or either discontinuing the diuretic temporarily or increasing the salt intake approximately one week prior to initiating an ACE inhibitor. Alternatively, the patient may remain under medical supervision for at least two hours after the first dose of the ACE inhibitor, or until blood pressure has stabilized.', 'Synergism', 'Monitoring of blood pressure, diuresis, electrolytes, and renal function is recommended during coadministration.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1455052, 'Sodium aurothiomalate', 'Lisinopril', 'Moderate', 'Concomitant ACE inhibitor therapy may potentiate the risk of nitritoid (vasomotor) reactions associated with injectable gold therapy. The mechanism of interaction is unknown, although investigators suggest that ACE inhibitors may unmask drug hypersensitivity reactions by preventing the breakdown of bradykinins.', 'DDInter', 1767317476, 'Patients on injectable gold therapy in combination with an ACE inhibitor should be monitored for symptoms of nitritoid reactions such as facial flushing, nausea, vomiting, and hypotension following an injection. Elderly patients in particular should be observed following treatment. Oxygen, intravenous fluids, epinephrine, steroids, and other supportive therapy may be necessary for treatment.', 'Others', 'Patients on injectable gold therapy in combination with an ACE inhibitor should be monitored for symptoms of nitritoid reactions such as facial flushing, nausea, vomiting, and hypotension following an injection.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1455053, 'Guanfacine', 'Lisinopril', 'Moderate', 'Use of guanfacine in combination with antihypertensive agents may result in additive pharmacodynamic effects such as hypotension and syncope.', 'DDInter', 1767317476, 'Caution is advised during coadministration of these agents. Dosage adjustments may be required and hemodynamic responses should be monitored during concomitant therapy. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their doctor if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia. Patients should also avoid driving or operating hazardous machinery until they know how the medications affect them.', 'Synergism', 'Caution is advised during coadministration of these agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1455054, 'Haloperidol', 'Lisinopril', 'Moderate', 'Phenothiazines and neuroleptic agents may potentiate the hypotensive effect of some medications secondary to their peripheral alpha-1 adrenergic blocking activity. Orthostatic hypotension and syncope associated with vasodilation may occur, particularly during initial dosing and/or parenteral administration of the phenothiazine or neuroleptic.', 'DDInter', 1767317476, 'Close clinical monitoring for development of hypotension is recommended if phenothiazines or neuroleptic agents are used in patients receiving antihypertensive medications or vasodilators. A lower starting dosage and slower titration of the phenothiazine or neuroleptic may be appropriate, especially in the elderly. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia. Patients should also avoid driving or operating hazardous machinery.', 'Synergism', 'Close clinical monitoring for development of hypotension is recommended if phenothiazines or neuroleptic agents are used in patients receiving antihypertensive medications or vasodilators.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1455055, 'Heparin', 'Lisinopril', 'Moderate', 'The concomitant use of angiotensin converting enzyme (ACE) inhibitors or angiotensin receptor blockers (ARBs) and heparin or low molecular weight heparins may increase the risk of hyperkalemia. ACE inhibitors, ARBs, and some heparins individually have been associated with increased potassium levels or hyperkalemia.', 'DDInter', 1767317476, 'Caution is advised if ACE inhibitors or ARBs are used with heparin or low molecular weight heparin. Serum potassium and renal function should be checked regularly.', 'Synergism', 'Caution is advised if ACE inhibitors or ARBs are used with heparin or low molecular weight heparin.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1455056, 'Diamorphine', 'Lisinopril', 'Moderate', 'Many psychotherapeutic and CNS-active agents (e.g., anxiolytics, sedatives, hypnotics, antidepressants, antipsychotics, opioids, alcohol, muscle relaxants) exhibit hypotensive effects, especially during initiation of therapy and dose escalation. Coadministration with antihypertensives and other hypotensive agents, in particular vasodilators and alpha-blockers, may result in additive effects on blood pressure and orthostasis.', 'DDInter', 1767317476, 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents. Avoid alcohol in patients receiving vasodilating antihypertensive drugs. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
