    ) VALUES (1455121, 'Pergolide', 'Lisinopril', 'Moderate', 'One patient, whose hypertension had been treated with lisinopril for three years, experienced a severe hypotensive episode after receiving a 0.05 mg dose of pergolide. The mechanism is unknown and causality has not been clearly established.', NULL, 'It is recommended that the clinician use caution when initiating pergolide therapy in patients who are receiving angiotensin converting enzyme inhibitors.', NULL, 'It is recommended that the clinician use caution when initiating pergolide therapy in patients who are receiving angiotensin converting enzyme inhibitors. Dose adjustments may be required.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
