    ) VALUES (1455213, 'Dobutamine', 'Lithium carbonate', 'Minor', 'Lithium may decrease the pressor effect of some sympathomimetic agents. The mechanism is unknown. Also, dopamine may increase the plasma clearance of lithium. Plasma lithium levels may be decreased.', NULL, '-', NULL, '-', 'Excretion', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
