    ) VALUES (1455214, 'Doxacurium', 'Lithium carbonate', 'Moderate', 'Limited data suggest that lithium may potentiate the pharmacologic effects of neuromuscular blocking agents. The exact mechanism of interaction is unknown.', NULL, 'Caution is advised if neuromuscular blocking agents are used in patients who have been treated with lithium.', NULL, 'Caution is advised if neuromuscular blocking agents are used in patients who have been treated with lithium. Patients should be monitored closely for development of respiratory depression, and life support should be readily available if needed.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
